	void StartLaser(unsigned short x, unsigned short y)
	{
		const int xOffset = 28;
		const int yOffset = 9;

		gotoxy(x + xOffset, y + yOffset);
		setcolor(LIGHTBLUE);
		printf("__");
		Sleep(10);
		printf("__");
		Sleep(10);

		for (int i = 0; i < 3; i++)
		{
			gotoxy(x + xOffset + i, y + yOffset);
			setcolor(0x00);
			printf("  ");
			setcolor(LIGHTBLUE);
			printf("__");
			printf("__");

			Sleep(10);
		}
		setcolor(0x00);
	}
	void ShootLaser(unsigned short x, unsigned short y, bool win, int kek)
	{
		const int xOffset = 31;
		const int yOffset = 9;
		int i;

		for (i = 0; i < kek; i++)
		{
			gotoxy(x + xOffset + i, y + yOffset);
			setcolor(0x00);
			printf("  ");
			setcolor(LIGHTBLUE);
			printf("__");
			printf("__");

			Sleep(10);
		}
		setcolor(0x00);

		gotoxy(x + xOffset + i + 1, y + yOffset);
		printf("    ");

		if (win)
		{
			WinRepublicEmblem(x + xOffset + i + 1, y + yOffset);
		}
	}

	void StartLaserReverse(unsigned short x, unsigned short y)
	{
		const int xOffset = -17;
		const int yOffset = 9;

		gotoxy(x + xOffset, y + yOffset);
		setcolor(LIGHTBLUE);
		printf("__");
		Sleep(10);
		printf("__");
		Sleep(10);

		for (int i = 0; i < 3; i++)
		{
			gotoxy(x + xOffset - i, y + yOffset);
			setcolor(LIGHTBLUE);
			printf("__");
			printf("__");
			setcolor(0x00);
			printf("  ");

			Sleep(10);
		}
	}
	void ShootLaserReverse(unsigned short x, unsigned short y, bool win, int kek)
	{
		const int xOffset = -20;
		const int yOffset = 9;
		int i;

		for (i = 0; i < kek; i++)
		{
			gotoxy(x + xOffset - i, y + yOffset);
			setcolor(LIGHTBLUE);
			printf("__");
			printf("__");
			setcolor(0x00);
			printf("    ");

			Sleep(10);
		}

		gotoxy(x + xOffset - i + 1, y + yOffset);
		printf("    ");

		if (win)
		{
			WinRepublicEmblem(x + xOffset - i + 1, y + yOffset);
		}
	}

	void WinRepublicEmblem(int x, int y)
	{
		animateRepublicEmblem(x - 10, y - 10);
	}


	int Xcoord()
	{
		return (MIDDLE - x - 1) - 16 / 2;
	}
	int XcoordReverse()
	{
		return (MIDDLE + x) - 16 / 2 + 1;
	}
	
protected:
	int  armor;		// reduces the damage Heros gets
	char block;		// is blocking active?
	const int distanceToTop = 13;
public:
	Meinz()
	{
		strcpy(autorName, "Ortner");
		strcpy(heroName, "CT-Chad");

		life	=	MAX_LIFE;
		damage	= 	STD_DAMAGE;
		armor	= 	STD_DAMAGE/2;
		width	=	9;
		move	=	14;
		block	=	0;
		range	=	160;
	}
	
	void Show()
	{
		if(side == 'l')
		{
			//Character on the left (facing right)
			drawCharacter(Xcoord(), distanceToTop);	//80 = 160/2 -> 160 = console width || 16/2 character width / 2 -> fixes goto offset
		}
		else
		{
			//Draw Character on the right (facing left)
			drawCharacterReverse(XcoordReverse(), distanceToTop);
		}
		setcolor(0x00);	//Set Color to black so the Base clear functions don't break
    }
	void Clear()
	{
		if(side == 'l')
		{
			//Character on the left (facing right)
			clearCharacter(Xcoord(), distanceToTop);	//80 = 160/2 -> 160 = console width || 16/2 character width / 2 -> fixes goto offset
		}
		else
		{
			//Clear Character on the right (facing left)
			clearCharacterReverse(XcoordReverse(), distanceToTop);
		}
	}

	void Move(int wohin)
	{
		Clear();
		if (wohin < 0)
		{
			for (int i = 0; i < move; i++)
			{
				if (MIDDLE - (x + width + 2) == 0)	//distance to window border || 2 cuz weird
				{
					break;
				}
				x++;
				if (side == 'l')
				{
					animateWalk(Xcoord(), distanceToTop, i);	//-1 cuz weird
				}
				else
				{
					animateWalk(XcoordReverse(), distanceToTop, i);
				}
			}
		}
		else
		{
			for (int i = 0; i < move; i++)
			{
				if ((x - width) + ((enemy->x - enemy->width)) == 0)	//Distance to enemy
				{
					break;
				}
				x--;
				if (side == 'l')
				{
					animateWalk(Xcoord(), distanceToTop, i);	//-1 cuz weird
				}
				else
				{
					animateWalk(XcoordReverse(), distanceToTop, i);
				}
			}
		}
		Show();
	}
	
	void ShowDeath()
	{
		//Custom death animation goes here
		if (life > 0) return;  // still alive

		int yy = (height + leg) / 2; // average height
		setcolor(RED);
		Linie(x - 3, yy, 7, 0, '+');
		Sleep(200);
		Linie(x - 4, yy + 2, 9, -4, '+');
		Sleep(200);
		Linie(x - 5, yy - 4, 11, +8, '+');
		Sleep(500);
		Linie(x - 3, yy, 7, 0, '.');
		Linie(x - 4, yy + 2, 9, -4, '.');
		Linie(x - 5, yy - 4, 11, +8, '.');
		Sleep(200);
		Clear();
		setcolor(RED);
		Linie(x - 3, yy, 7, 0, '.');
		Linie(x - 4, yy + 2, 9, -4, '.');
		Linie(x - 5, yy - 4, 11, +8, '.');
		Sleep(500);

		setcolor(0x00);
	}
	void ClearDeath()
	{
		//Clear death animation
		int yy = (height + leg) / 2; // average height
		setcolor(0x00);
		Linie(x - 3, yy, 7, 0, ' ');
		Linie(x - 4, yy + 2, 9, -4, ' ');
		Linie(x - 5, yy - 4, 11, +8, ' ');
	}
	
