		void animateWalk(unsigned short x, unsigned short y)
		{
			animateWalk0(x, y);
			Sleep(100);
			clearAnimateWalk0(x, y);
			animateWalk1(x, y);
			Sleep(100);
			clearAnimateWalk1(x, y);
			animateWalk2(x, y);
			Sleep(100);
			clearAnimateWalk2(x, y);
			animateWalk2(x, y);
			Sleep(100);
			clearAnimateWalk2(x, y);
			animateWalk1(x, y);
			Sleep(100);
			clearAnimateWalk1(x, y);
		}
		void animateReverseWalk(unsigned short x, unsigned short y)
		{
			animateReverseWalk0(x, y);
			Sleep(100);
			clearAnimateReverseWalk0(x, y);
			animateReverseWalk1(x, y);
			Sleep(100);
			clearAnimateReverseWalk1(x, y);
			animateReverseWalk2(x, y);
			Sleep(100);
			clearAnimateReverseWalk2(x, y);
			animateReverseWalk2(x, y);
			Sleep(100);
			clearAnimateReverseWalk2(x, y);
			animateReverseWalk1(x, y);
			Sleep(100);
			clearAnimateReverseWalk1(x, y);
		}
	
protected:
	int  armor;		// reduces the damage Heros gets
	char block;		// is blocking active?
	const int distanceToTop = 13;
public:
	Meinz()
	{
		strcpy(autorName, "Ortner");
		strcpy(heroName, "CT-Chad");

		life	=	MAX_LIFE;
		damage	= 	STD_DAMAGE;
		armor	= 	STD_DAMAGE/2;
		width	=	9;
		move	=	14;
		block	=	0;
		range	=	160;
	}
	
	void Show()
	{
		if(side == 'l')
		{
			//Character on the left (facing right)
			drawCharacter((80 - x - 1) - 16 / 2, distanceToTop);	//80 = 160/2 -> 160 = console width || 16/2 character width / 2 -> fixes goto offset
		}
		else
		{
			//Draw Character on the right (facing left)
			drawCharacterReverse((80 + x) - 16 / 2 + 1, distanceToTop);
		}
		setcolor(0x00);	//Set Color to black so the Base clear functions don't break
    }
	void Clear()
	{
		if(side == 'l')
		{
			//Character on the left (facing right)
			clearCharacter((80 - x - 1) - 16 / 2, distanceToTop);	//80 = 160/2 -> 160 = console width || 16/2 character width / 2 -> fixes goto offset
		}
		else
		{
			//Clear Character on the right (facing left)
			clearCharacterReverse((80 + x) - 16 / 2 + 1, distanceToTop);
		}
	}

	void Move(int wohin)
	{
		Clear();
		if (wohin < 0)
		{
			for (int i = 0; i < move; i++)
			{
				if (MIDDLE - (x + width + 2) == 0)	//distance to window border || 2 cuz weird
				{
					break;
				}
				x++;
				if (side == 'l')
				{
					animateWalk((80 - x - 1) - 16 / 2, distanceToTop, i);	//-1 cuz weird
				}
				else
				{
					animateWalk((80 + x) - 16 / 2 + 1, distanceToTop, i);
				}
			}
		}
		else
		{
			for (int i = 0; i < move; i++)
			{
				if ((x - width) + ((enemy->x - enemy->width)) == 0)	//Distance to enemy
				{
					break;
				}
				x--;
				if (side == 'l')
				{
					animateWalk((80 - x - 1) - 16 / 2, distanceToTop, i);	//-1 cuz weird
				}
				else
				{
					animateWalk((80 + x) - 16 / 2 + 1, distanceToTop, i);
				}
			}
		}
		Show();
	}
	
